/**
 * Socket Service - Integrated from services-socket.js
 */
class SocketService {
    constructor(config = {}) {
        this.config = {
            autoConnect: true,
            reconnectAttempts: 5,
            reconnectDelay: 1000,
            heartbeatInterval: 30000,
            ...config
        };
        this.socket = null;
        this.connected = false;
        this.reconnectCount = 0;
        this.eventListeners = new Map();
        this.subscriptions = new Set();
        this.heartbeatTimer = null;
        this.logger = console;
    }

    async initialize() {
        if (this.config.autoConnect) {
            await this.connect();
        }
    }

    async connect(url = 'ws://localhost:8080') {
        if (this.connected) return;

        return new Promise((resolve, reject) => {
            try {
                this.socket = new WebSocket(url);

                this.socket.onopen = () => {
                    this.connected = true;
                    this.reconnectCount = 0;
                    this.startHeartbeat();
                    this.logger.log('WebSocket connected');
                    resolve();
                };

                this.socket.onmessage = (event) => {
                    this.handleMessage(event.data);
                };

                this.socket.onclose = () => {
                    this.connected = false;
                    this.stopHeartbeat();
                    this.logger.log('WebSocket disconnected');
                    if (this.config.autoConnect) {
                        this.attemptReconnection();
                    }
                };

                this.socket.onerror = (error) => {
                    this.logger.error('WebSocket error:', error);
                    reject(error);
                };

            } catch (error) {
                this.logger.error('Failed to create WebSocket connection:', error);
                reject(error);
            }
        });
    }

    disconnect() {
        if (this.socket) {
            this.socket.close();
        }
        this.connected = false;
        this.stopHeartbeat();
    }

    sendMessage(message) {
        if (this.connected && this.socket) {
            const messageStr = typeof message === 'string' ? message : JSON.stringify(message);
            this.socket.send(messageStr);
            return true;
        }
        return false;
    }

    subscribe(channel) {
        if (!this.subscriptions.has(channel)) {
            this.subscriptions.add(channel);
            this.sendMessage({ type: 'subscribe', channel });
        }
    }

    unsubscribe(channel) {
        if (this.subscriptions.has(channel)) {
            this.subscriptions.delete(channel);
            this.sendMessage({ type: 'unsubscribe', channel });
        }
    }

    handleMessage(data) {
        try {
            const message = JSON.parse(data);
            // Trigger event listeners for message type
            const listeners = this.eventListeners.get(message.type) || [];
            listeners.forEach(listener => listener(message));
        } catch (error) {
            this.logger.error('Failed to parse WebSocket message:', error);
        }
    }

    addEventListener(eventType, listener) {
        if (!this.eventListeners.has(eventType)) {
            this.eventListeners.set(eventType, []);
        }
        this.eventListeners.get(eventType).push(listener);
    }

    removeEventListener(eventType, listener) {
        const listeners = this.eventListeners.get(eventType);
        if (listeners) {
            const index = listeners.indexOf(listener);
            if (index > -1) {
                listeners.splice(index, 1);
            }
        }
    }

    startHeartbeat() {
        this.heartbeatTimer = setInterval(() => {
            if (this.connected) {
                this.sendMessage({ type: 'ping' });
            }
        }, this.config.heartbeatInterval);
    }

    stopHeartbeat() {
        if (this.heartbeatTimer) {
            clearInterval(this.heartbeatTimer);
            this.heartbeatTimer = null;
        }
    }

    async attemptReconnection() {
        if (this.reconnectCount >= this.config.reconnectAttempts) {
            this.logger.error('Max reconnection attempts reached');
            return;
        }

        this.reconnectCount++;
        this.logger.log(`Attempting reconnection ${this.reconnectCount}/${this.config.reconnectAttempts}`);

        setTimeout(async () => {
            try {
                await this.connect();
            } catch (error) {
                this.logger.error('Reconnection failed:', error);
            }
        }, this.config.reconnectDelay * this.reconnectCount);
    }

    getStatus() {
        return {
            connected: this.connected,
            reconnectCount: this.reconnectCount,
            subscriptions: Array.from(this.subscriptions)
        };
    }

    destroy() {
        this.disconnect();
        this.eventListeners.clear();
        this.subscriptions.clear();
    }
}

